    private void curso_cargaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_curso_cargaKeyTyped
        char c = evt.getKeyChar();
        if(!Character.isDigit(c)){
            evt.consume();
        }
    }//GEN-LAST:event_curso_cargaKeyTyped

    private void limiteKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_limiteKeyTyped
        char c = evt.getKeyChar();
        if(!Character.isDigit(c)){
            evt.consume();
        }
    }//GEN-LAST:event_limiteKeyTyped

