    private void btn_adicActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_adicActionPerformed
        Curso curso = new Curso();
        cadCurso adc = new cadCurso();
        curso.setNome(nome_curso_adc.getText());
        
        boolean v1,v2;
        
        v1 = curso.veriflimite(Integer.parseInt(limite.getText()));
        v2 = curso.verifCarga(Integer.parseInt(curso_carga.getText())); 
        
        if(v1 == false){
            JOptionPane.showMessageDialog(null, "O limite de alunos deve ser maior ou igual a 1!");
        }else{
            curso.setLimite(limite.getText());
        }
        
        if(v2 == false){
            JOptionPane.showMessageDialog(null, "A carga horária deve ser maior que 20 horas!");
        }else{
            curso.setCarga(curso_carga.getText());
        }
        
        if(v1 == true && v2 == true){
            adc.adicionarCurso(curso);
            JOptionPane.showMessageDialog(null, "Curso cadastrado com sucesso");
        }
    }//GEN-LAST:event_btn_adicActionPerformed

    private void btn_rmActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_rmActionPerformed
        new cadCurso().removerCurso(nome_curso_adc.getText());
        new cadCurso().removerAlunos(nome_curso_adc.getText());
        JOptionPane.showMessageDialog(null, "Curso removido com sucesso!");
    }//GEN-LAST:event_btn_rmActionPerformed

